/// \file
/// This file declares the targeting of the RegisterBankInfo class for Valida.
/// \todo This should be generated by TableGen.
//===----------------------------------------------------------------------===//

#ifndef LLVM_LIB_TARGET_VALIDA_VALIDAREGISTERBANKINFO_H
#define LLVM_LIB_TARGET_VALIDA_VALIDAREGISTERBANKINFO_H

#include "llvm/CodeGen/RegisterBankInfo.h"

#define GET_REGBANK_DECLARATIONS
#include "ValidaGenRegisterBank.inc"

namespace llvm {

class TargetRegisterInfo;

class ValidaGenRegisterBankInfo : public RegisterBankInfo {
protected:
#define GET_TARGET_REGBANK_CLASS
#include "ValidaGenRegisterBank.inc"
};

/// This class provides the information for the target register banks.
class ValidaRegisterBankInfo final : public ValidaGenRegisterBankInfo {
public:
  ValidaRegisterBankInfo(const TargetRegisterInfo &TRI);

  const RegisterBank &getRegBankFromRegClass(const TargetRegisterClass &RC,
                                             LLT) const override;

  const InstructionMapping &
  getInstrMapping(const MachineInstr &MI) const override;
};
} // end namespace llvm
#endif // LLVM_LIB_TARGET_VALIDA_VALIDAREGISTERBANKINFO_H
